mapper.select:
SELECT dc.id, IFNULL(dc.name,dc.nick_name) AS name, dc.phone, dc.open_id AS openId, dc.head_cover AS headCover, dc.nick_name AS nickName, dbp.name AS `customerGroup.bonusPlanName`, dbp.id AS `customerGroup.bonusPlanId`, dw.available_money AS `wallet.sumMoney`, dg.name AS groupName, dcg.serial_number AS `customerGroup.serialNumber`, dcg.status AS `customerGroup.status`, dcg.expire_date AS `customerGroup.expireDate`, dcg.create_date AS createDate, dcg.group_id AS `customerGroup.groupId`, dcg.parent_id AS `customerGroup.parentId`, dcg.type AS `customerGroup.type`, dcg.id AS `customerGroup.id`, dcg.create_date AS `customerGroup.createDate`, dcg.total_new AS `customerGroup.totalNew`, packages.cnt AS `customerGroup.packageCount`, grpByParent.cnt AS myCustomerCnt, employeeInfo.employee_id AS employeeId, dbw.available_bonus AS `bonusWallet.availableBonus`, IFNULL(orderToConsume.cnt,0) AS `customerGroup.orderToConsumeCnt` FROM dc LEFT JOIN d_customer_group dcg ON dcg.customer_id = dc.id LEFT JOIN d_group dg ON dg.id = dcg.group_id LEFT JOIN d_bonus_plan dbp ON dbp.id = dcg.bonus_plan_id LEFT JOIN d_wallet dw ON dw.id = dcg.wallet_id LEFT JOIN d_bonus_wallet dbw ON dbw.id = dcg.bonus_wallet_id LEFT JOIN ( SELECT customer_group_id , count(*) cnt FROM d_order WHERE status IN (2,3) AND goods_type_id = 2 GROUP BY customer_group_id ) packages ON packages.customer_group_id = dcg.id LEFT JOIN ( SELECT customer_group_id , count(*) cnt FROM d_order WHERE status =2 GROUP BY customer_group_id ) orderToConsume ON orderToConsume.customer_group_id = dcg.id LEFT JOIN ( SELECT parent_id, count(*) cnt FROM d_customer_group WHERE status = 1
AND group_id = #{groupId}
GROUP BY parent_id ) grpByParent ON grpByParent.parent_id = dcg.id LEFT JOIN ( SELECT employee_id,customer_group_id FROM d_employee_customer WHERE status = 1 ) employeeInfo ON employeeInfo.customer_group_id = dcg.id WHERE (dcg.status != -2 OR dcg.status IS NULL)
AND dg.id = #{groupId}
AND dg.id = #{customer.groupId}
AND dc.phone = #{exactPhone}
AND (dc.phone LIKE CONCAT('%',#{nameOrPhone},'%') OR dc.name LIKE CONCAT('%',#{nameOrPhone},'%'))
AND (dc.name LIKE CONCAT('%',#{customer.name},'%') OR dc.nick_name LIKE CONCAT('%',#{customer.name},'%'))
AND dc.open_id = #{customer.openId}
AND dc.phone LIKE CONCAT('%',#{customer.phone},'%')
AND dcg.serial_number LIKE CONCAT('%',#{customer.customerGroup.serialNumber},'%')
AND dcg.type = #{customer.customerGroup.type}
AND dcg.status = #{customer.customerGroup.status}
AND dcg.bonus_plan_id = #{customer.customerGroup.bonusPlanId}
AND dcg.parent_id = #{customer.customerGroup.parentId}
AND dcg.parent_id IN (SELECT dcg.id FROM d_customer_group dcg LEFT JOIN d_customer dc ON dc.id = dcg.customer_id WHERE dc.open_id = #{parentOpenId})
AND dcg.id IN
#{id}
ORDER BY dcg.status DESC,dcg.id DESC, dc.id desc
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM   dc
LEFT JOIN d_customer_group dcg ON dcg.customer_id = dc.id
WHERE
	(dcg.status != -2 OR dcg.status IS NULL)

		AND dcg.group_id = #{groupId}


		AND dcg.group_id = #{customer.groupId}


		AND dc.phone = #{exactPhone}


		AND (dc.phone LIKE CONCAT('%',#{nameOrPhone},'%') OR dc.name LIKE CONCAT('%',#{nameOrPhone},'%'))


		AND (dc.name LIKE CONCAT('%',#{customer.name},'%') OR dc.nick_name LIKE CONCAT('%',#{customer.name},'%'))


		AND dc.open_id = #{customer.openId}


		AND dc.phone LIKE CONCAT('%',#{customer.phone},'%')


		AND dcg.serial_number LIKE CONCAT('%',#{customer.customerGroup.serialNumber},'%')


		AND dcg.type = #{customer.customerGroup.type}


		AND dcg.status = #{customer.customerGroup.status}


		AND dcg.bonus_plan_id = #{customer.customerGroup.bonusPlanId}


		AND dcg.parent_id = #{customer.customerGroup.parentId}


		AND dcg.parent_id IN (SELECT dcg.id FROM d_customer_group dcg LEFT JOIN d_customer dc ON dc.id = dcg.customer_id WHERE dc.open_id = #{parentOpenId})


		AND dcg.id IN

			#{id}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,name = #{name}


		,nick_name = #{nickName}


		,phone = #{phone}


		,open_id = #{openId}


		,`head_cover` = #{headCover}

WHERE
	id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

		#{id}
