mapper.select:
SELECT dc.id, dc.name, dcg.serial_number AS `customerGroup.serialNumber`, dcg.status AS `customerGroup.status`, dc.phone, dbp.name AS `customerGroup.bonusPlanName`, dbp.id AS `customerGroup.bonusPlanId`, 0 AS packageCount, dw.available_money AS `wallet.sumMoney`, dg.name AS groupName, dcg.expire_date AS `customerGroup.expireDate`, dcg.create_date AS createDate, dcg.group_id AS `customerGroup.groupId`, dcg.type AS `customerGroup.type`, dcg.id AS `customerGroup.id`, packages.cnt AS `customerGroup.packageCount` FROM dc LEFT JOIN d_customer_group dcg ON dcg.customer_id = dc.id LEFT JOIN d_group dg ON dg.id = dcg.group_id LEFT JOIN d_bonus_plan dbp ON dbp.id = dcg.bonus_plan_id LEFT JOIN d_wallet dw ON dw.id = dcg.wallet_id LEFT JOIN ( SELECT customer_group_id , count(*) cnt FROM d_order WHERE status IN (2,3) AND goods_type_id = 2 GROUP BY customer_group_id ) packages ON packages.customer_group_id = dcg.id WHERE dcg.status != -2
AND dg.id = #{groupId}
AND dc.phone = #{exactPhone}
AND (dc.phone LIKE CONCAT('%',#{nameOrPhone},'%') OR dc.name LIKE CONCAT('%',#{nameOrPhone},'%'))
AND dc.name LIKE CONCAT('%',#{customer.name},'%')
AND dc.phone LIKE CONCAT('%',#{customer.phone},'%')
AND dcg.serial_number LIKE CONCAT('%',#{customer.customerGroup.serialNumber},'%')
AND dcg.type = #{customer.customerGroup.type}
AND dcg.status = #{customer.customerGroup.status}
AND dcg.bonus_plan_id = #{customer.customerGroup.bonusPlanId}
AND dcg.id IN
#{id}
ORDER BY dcg.status DESC
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM   dc
LEFT JOIN d_customer_group dcg ON dcg.customer_id = dc.id
WHERE
	dcg.status != -2

		AND dcg.group_id = #{groupId}


		AND dc.phone = #{exactPhone}


		AND (dc.phone LIKE CONCAT('%',#{nameOrPhone},'%') OR dc.name LIKE CONCAT('%',#{nameOrPhone},'%'))


		AND dc.name LIKE CONCAT('%',#{customer.name},'%')


		AND dc.phone LIKE CONCAT('%',#{customer.phone},'%')


		AND dcg.serial_number LIKE CONCAT('%',#{customer.customerGroup.serialNumber},'%')


		AND dcg.type = #{customer.customerGroup.type}


		AND dcg.status = #{customer.customerGroup.status}


		AND dcg.bonus_plan_id = #{customer.customerGroup.bonusPlanId}


		AND dcg.id IN

			#{id}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,name = #{name}


		,phone = #{phone}


		,open_id = #{openId}


		,`head_cover` = #{headCover}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

		#{id}
