mapper.select:
SELECT dcg.id, IFNULL(dc.name,dc.nick_name) AS shareHolderName, dc.phone, dg.name AS groupName, childrenCnt.allCnt AS allCnt, childrenCnt.monthCnt AS monthCnt, childrenCnt.consumeCnt AS consumeCnt, childrenCnt.consumeCnt/childrenCnt.monthCnt AS consumeRate, dbw.available_bonus AS availableBonus FROM d_customer_group dcg LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_group dg ON dg.id = dcg.group_id LEFT JOIN d_bonus_wallet dbw ON dbw.id = dcg.bonus_wallet_id LEFT JOIN ( SELECT COUNT(*) allCnt, COUNT(create_date BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59') OR NULL) monthCnt, COUNT(orderCnt.cnt) consumeCnt, parent_id FROM d_customer_group LEFT JOIN ( SELECT COUNT(*) cnt, customer_group_id FROM d_order GROUP BY customer_group_id ) orderCnt ON orderCnt.customer_group_id = d_customer_group.id GROUP BY parent_id ) childrenCnt ON childrenCnt.parent_id = dcg.id WHERE dcg.type = 2
AND dcg.group_id = #{groupId}
ORDER BY ${sortType} DESC

SELECT dcg.id, IFNULL(dc.name,dc.nick_name) AS customerName, IFNULL(parentC.name,parentC.nick_name) AS parentName, dc.phone, COUNT(doc.create_date BETWEEN CONCAT(#{startDate},' 00:00:00') AND CONCAT(#{endDate},' 23:59:59') OR NULL) AS monthCnt, MAX(doc.create_date) AS lastConsumeDate FROM d_customer_group dcg LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_customer_group parent ON parent.id = dcg.parent_id LEFT JOIN d_customer parentC ON parentC.id = parent.customer_id LEFT JOIN d_order do ON do.customer_group_id = dcg.id LEFT JOIN d_order_consumption doc ON doc.order_id = do.id WHERE dcg.type = 1
AND dcg.group_id = #{groupId}
GROUP BY dcg.id ORDER BY dcg.id
