mapper.select:
SELECT id, name, phone, open_id AS openId, serial_number AS serialNumber, nick_name AS nickName, group_id AS groupId, status, create_date AS createDate, creator, update_date AS updateDate, updater FROM WHERE 1=1
AND group_id = #{employee.groupId}
AND status = #{employee.status}
AND name LIKE CONCAT('%',#{employee.name},'%')
AND phone LIKE CONCAT('%',#{employee.phone},'%')
AND open_id =#{employee.openId}
ORDER BY status DESC,id
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM 
WHERE
	1=1

		AND group_id = #{employee.groupId}


		AND status = #{employee.status}


		AND name LIKE CONCAT('%',#{employee.name},'%')


		AND phone LIKE CONCAT('%',#{employee.phone},'%')


		AND open_id =#{employee.openId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,name = #{name}


		,phone = #{phone}


		,open_id = #{openId}


		,`session_key` = #{sessionKey}


		,`nick_name` = #{nickName}


		,`head_cover` = #{headCover}


		,status = #{status}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

