mapper.select:
SELECT de.id, de.name, de.phone, de.open_id AS openId, de.serial_number AS serialNumber, de.nick_name AS nickName, de.group_id AS groupId, de.status, de.gender, de.employee_level AS employeeLevel, de.join_date AS joinDate, de.identify_no AS identifyNo, de.create_date AS createDate, de.creator, de.update_date AS updateDate, de.updater, dg.name AS groupName FROM de LEFT JOIN d_group dg ON dg.id = de.group_id WHERE 1=1
AND de.group_id = #{employee.groupId}
AND de.status = #{employee.status}
AND de.name LIKE CONCAT('%',#{employee.name},'%')
AND de.phone LIKE CONCAT('%',#{employee.phone},'%')
AND de.open_id =#{employee.openId}
ORDER BY status DESC,id
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM 
WHERE
	1=1

		AND group_id = #{employee.groupId}


		AND status = #{employee.status}


		AND name LIKE CONCAT('%',#{employee.name},'%')


		AND phone LIKE CONCAT('%',#{employee.phone},'%')


		AND open_id =#{employee.openId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,name = #{name}


		,phone = #{phone}


		,open_id = #{openId}


		,`session_key` = #{sessionKey}


		,`nick_name` = #{nickName}


		,`head_cover` = #{headCover}


		,status = #{status}


		,gender = #{gender}


		,employee_no = #{employeeLevel}


		,join_date = #{joinDate}


		,identify_no = #{identifyNo}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

		#{id}
