mapper.select:
SELECT dgs.id, dgs.name, dg.name AS groupName, dgs.desc, dgs.detail, dgs.profit_rate AS profitRate, dgs.price, dgs.unit, dgs.thumbnail, dgt.name AS goodsTagName, dgs.status, dgs.create_date AS createDate FROM dgs LEFT JOIN d_group dg ON dg.id = dgs.group_id LEFT JOIN d_goods_tag dgt ON dgt.id = dgs.goods_tag_id WHERE dgs.status != -2
AND dgs.name LIKE CONCAT('%',#{goods.name},'%')
AND dgs.status = #{goods.status}
AND dgs.goods_tag_id = #{goods.goodsTagId}
AND dgs.group_id = #{goods.groupId}
ORDER BY status DESC
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM 
WHERE
	status != -2

		AND name LIKE CONCAT('%',#{goods.name},'%')


		AND status = #{goods.status}


		AND goods_tag_id = #{goods.goodsTagId}


		AND group_id = #{goods.groupId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

