mapper.select:
SELECT dgs.id, dgs.name, dgs.combine, dgs.type, dg.name AS groupName, dgs.status, dgs.cnt, dgs.goods_item_type_cnt AS goodsItemTypeCnt, dgs.max_cnt_per_cus AS maxCntPerCus, dgs.scope, dgs.sum_price AS sumPrice, dgs.expire_date AS expireDate, dgs.desc, dgs.serial_number AS serialNumber, dgs.cover_img AS coverImg, dgs.create_date AS createDate FROM dgs LEFT JOIN d_group dg ON dg.id = dgs.group_id WHERE dgs.status != -2
AND dgs.type = #{goods.type}
AND dgs.name LIKE CONCAT('%',#{goods.name},'%')
AND dgs.status = #{goods.status}
AND gds.serial_number LIKE CONCAT('%',#{goods.serialNumber},'%')
AND EXISTS (SELECT 1 FROM d_goods_item WHERE dgs.id = goods_id AND name LIKE CONCAT('%',#{goodsItem.name},'%'))
AND dgs.status = #{goodsItem.status}
AND dgs.group_id = #{goods.groupId}
AND EXISTS (SELECT 1 FROM d_goods_item WHERE dgs.id = goods_id AND goods_tag_id = #{goodsItem.goodsTagId})
AND dgs.group_id = #{goodsItem.groupId}
AND dgs.group_id = #{groupId}
ORDER BY status DESC
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM  dgs
WHERE
	dgs.status != -2

		AND dgs.type = #{goods.type}


		AND dgs.name LIKE CONCAT('%',#{goods.name},'%')


		AND dgs.status = #{goods.status}


		AND EXISTS
		(SELECT 1 FROM d_goods_item WHERE dgs.id = goods_id AND name LIKE CONCAT('%',#{goodsItem.name},'%'))


		AND dgs.status = #{goodsItem.status}


		AND dgs.group_id = #{goods.groupId}


		AND EXISTS
		(SELECT 1 FROM d_goods_item WHERE dgs.id = goods_id AND goods_tag_id = #{goodsItem.goodsTagId})


		AND dgs.group_id = #{goodsItem.groupId}


		AND dgs.group_id = #{groupId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}
	,goods_item_type_cnt = (SELECT COUNT(*) FROM d_goods_item WHERE goods_id = #{id})

		,name = #{name}


		,`desc` = #{desc}


		,status = #{status}


		,cnt = #{cnt}


		,cover_img = #{coverImg}


		,sum_price = #{sumPrice}


		,expire_month_cnt = #{expireMonthCnt}


		,expire_date = #{expireDate}


		,`scope` = #{scope}


		,max_cnt_per_cus = #{maxCntPerCus}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

		#{id}
