mapper.select:
SELECT dgs.id, dgs.name, dgs.combine, dgs.type, dg.name AS groupName, dgs.status, dgs.create_date AS createDate FROM dgs LEFT JOIN d_group dg ON dg.id = dgs.group_id WHERE dgs.status != -2
AND EXISTS (SELECT 1 FROM d_group_item WHERE dgs.id = goods_id AND name LIKE CONCAT('%',#{goods.name},'%')
AND dgs.status = #{goods.status}
AND EXISTS (SELECT 1 FROM d_group_item WHERE dgs.id = goods_id AND goods_tag_id = #{goods.goodsTagId}
AND dgs.group_id = #{goods.groupId}
ORDER BY status DESC
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM  dgs
WHERE
	dgs.status != -2

		AND EXISTS
		(SELECT 1 FROM d_group_item WHERE dgs.id = goods_id AND name LIKE CONCAT('%',#{goods.name},'%')


		AND dgs.status = #{goods.status}


		AND EXISTS
		(SELECT 1 FROM d_group_item WHERE dgs.id = goods_id AND goods_tag_id = #{goods.goodsTagId}


		AND dgs.group_id = #{goods.groupId}

ORDER BY status DESC

	LIMIT #{start}, #{rows}

