mapper.select:
SELECT dgi.id, dgi.goods_id AS goodsId, dgi.name, dg.name AS groupName, dgi.desc, dgi.detail, dgi.profit_rate AS profitRate, dgi.price, dgi.unit, dgi.thumbnail, dgt.name AS goodsTagName, dgt.id AS goodsTagId, dgi.show_price AS showPrice, dgi.create_date AS createDate FROM dgi LEFT JOIN d_goods dgs ON dgs.id = dgi.goods_id LEFT JOIN d_group dg ON dg.id = dgs.group_id LEFT JOIN d_goods_tag dgt ON dgt.id = dgi.goods_tag_id WHERE 1=1
AND dgi.goods_id IN
#{goodsId}
LIMIT #{start}, #{rows}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,name = #{name}


		,profit_rate = #{profitRate}


		,goods_tag_id = #{goodsTagId}


		,price = #{price}


		,show_price = #{showPrice}


		,unit = #{unit}


		,thumbnail = #{thumbnail}


		,`desc` = #{desc}


		,`detail` = #{detail}

WHERE id = #{id}
