mapper.select:
SELECT dg.id, dg.name, dg.status, address AS address, owner_name AS ownerName, owner_phone AS ownerPhone, dg.city_code AS cityCode, dg.create_date AS createDate, ssca.city_ch_name AS cityName FROM dg LEFT JOIN s_support_city_amap ssca ON ssca.city_code = dg.city_code
AND dg.status = #{group.status}
AND dg.name LIKE CONCAT('%',#{group.name},'%')
AND dg.id = #{groupId}
ORDER BY create_date
LIMIT #{start}, #{rows}

SELECT
	COUNT(*)
FROM 


		AND status = #{group.status}


		AND name LIKE CONCAT('%',#{group.name},'%')


		AND id = #{groupId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}

		,name = #{name}


		,status = #{status}


		,address = #{address}


		,owner_name = #{ownerName}


		,owner_phone = #{ownerPhone}


		,city_code = #{cityCode}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

		#{id}
