mapper.select:
SELECT dg.id, dg.parent_id AS parentId, dg.name, dg.status, address AS address, owner_name AS ownerName, owner_phone AS ownerPhone, dg.desc, dg.thumbnail, dg.detail_cover_img AS detailCoverImg, dg.detail_img AS detailImg, dg.video, dg.video_cover_img AS videoCoverImg, dg.video_list AS videoList, dg.`index`, dg.open_rules AS openRules, dg.lng, dg.lat, dg.city_code AS cityCode, dg.create_date AS createDate, ssca.city_ch_name AS cityName FROM dg LEFT JOIN s_support_city_amap ssca ON ssca.city_code = dg.city_code WHERE dg.status != -2
AND dg.status = #{group.status}
AND dg.name LIKE CONCAT('%',#{group.name},'%')
AND dg.owner_name LIKE CONCAT('%',#{group.ownerName},'%')
AND dg.id = #{groupId}
ORDER BY status DESC, `index`
LIMIT #{start}, #{rows}

SELECT COUNT(*) FROM WHERE status != -2
AND status = #{group.status}
AND name LIKE CONCAT('%',#{group.name},'%')
AND owner_name LIKE CONCAT('%',#{group.ownerName},'%')
AND id = #{groupId}

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
