		,lng = #{lng}


		,lat = #{lat}


		,`desc` = #{desc}


		,thumbnail = #{thumbnail}


		,detail_cover_img = #{detailCoverImg}


		,detail_img = #{detailImg}


		,video = #{video}


		,`index` = #{index}


		, video_cover_img = #{videoCoverImg}


		, video_list = #{videoList}

WHERE id = #{id}

UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,status = #{status}
WHERE
	id IN

