mapper.select:
SELECT doc.id, doc.order_id AS orderId, doc.goods_id AS goodsId, doc.combine AS combine, doc.create_date AS createDate, do.goods_type_id AS order_goodsTypeId, do.goods_combine AS order_goodsCombine, do.goods_name AS order_goodsName, doic.id AS iid, doic.order_item_id AS orderItemId, doic.bef_sum_cnt AS befSumCnt, doic.bef_used_cnt AS befUsedCnt, doic.bef_available_cnt AS befAvailableCnt, doic.cnt, doic.aft_sum_cnt AS aftSumCnt, doic.aft_used_cnt AS aftUsedCnt, doic.aft_available_cnt AS aftAvailableCnt, doic.create_date AS icreateDate, doi.goods_name AS goodsName, doi.goods_price AS goodsPrice, doi.goods_unit AS goodsUnit, dg.name AS groupName, doic.create_date AS icreateDate, doic.order_consumption_id AS orderConsumptionId FROM doc LEFT JOIN d_order_item_consumption doic ON doic.order_consumption_id = doc.id LEFT JOIN d_order_item doi ON doi.id = doic.order_item_id LEFT JOIN d_order do ON do.id = doc.order_id LEFT JOIN d_customer_group dcg ON dcg.id = do.customer_group_id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_group dg ON dg.id = dcg.group_id WHERE 1=1
AND dc.open_id = #{openId}
AND doc.order_id = #{orderConsumption.orderId}
ORDER BY doc.id DESC
LIMIT #{start}, #{rows}

SELECT COUNT(*) FROM doc LEFT JOIN d_order_item_consumption doic ON doic.order_consumption_id = doc.id LEFT JOIN d_order_item doi ON doi.id = doic.order_item_id LEFT JOIN d_order do ON do.id = doc.order_id LEFT JOIN d_customer_group dcg ON dcg.id = do.customer_group_id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_group dg ON dg.id = dcg.group_id WHERE 1=1
AND dc.open_id = #{openId}
AND doc.order_id = #{orderConsumption.orderId}
