mapper.select:
SELECT dop.id, dop.order_batch_id AS orderBatchId, dop.order_id AS orderId, dop.sum_money AS sumMoney, dop.actually_money AS actuallyMoney, dop.pay_type AS payType, dop.create_date AS createDate, do.goods_type_id AS order_goodsTypeId, do.goods_combine AS order_goodsCombine, do.goods_name AS order_goodsName, dop.pay_type AS order_payType, do.order_cnt AS orderCnt, do.goods_type_name AS order_goodsTypeName, dopi.id AS iid, dopi.order_id AS iorderId, dopi.type, dopi.amount, dg.name AS groupName FROM dop LEFT JOIN d_order_pay_item dopi ON dopi.order_pay_id = dop.id LEFT JOIN d_order do ON do.id = dop.order_id LEFT JOIN d_customer_group dcg ON dcg.id = do.customer_group_id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_group dg ON dg.id = dcg.group_id WHERE 1=1
AND dop.order_id = #{orderPay.orderId}
AND dc.open_id = #{openId}
ORDER BY dop.id DESC
LIMIT #{start}, #{rows}

SELECT COUNT(*) FROM dop LEFT JOIN d_order_pay_item dopi ON dopi.order_pay_id = dop.id LEFT JOIN d_order do ON do.id = dop.order_id LEFT JOIN d_customer_group dcg ON dcg.id = do.customer_group_id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id WHERE 1=1
AND dop.order_id = #{orderPay.orderId}
AND dc.open_id = #{openId}
