mapper.select:
SELECT dc.name AS 'customer.name', dc.phone AS 'customer.phone', dcg.serial_number AS 'customer.customerGroup.serialNumber', dcg.type AS 'customer.customerGroup.type', dwd.money_change AS moneyChange, dwd.bef_available_money AS befAvailableMoney, dwd.create_date AS createDate, dg.name AS groupName, nu.username AS creator, dbp.bonus_rate AS 'customer.bonusPlan.bonusRate', dcg.expire_date AS 'customer.customerGroup.expireDate' FROM d_wallet_detail dwd LEFT JOIN d_wallet dw ON dwd.wallet_id = dw.id LEFT JOIN d_customer_group dcg ON dcg.wallet_id = dw.id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_bonus_plan dbp ON dbp.id = dcg.bonus_plan_id LEFT JOIN d_group dg ON dg.id = dcg.group_id LEFT JOIN ns_users nu ON nu.id = dwd.creator WHERE 1=1
AND dcg.group_id = #{groupId}
AND dwd.change_type = #{changeType}
AND dcg.id = #{customerGroupId}
AND dc.name LIKE CONCAT('%',#{name},'%')
AND dc.open_id = #{openId}
AND dcg.serial_number LIKE CONCAT('%',#{serialNumber},'%')
AND dc.phone LIKE CONCAT('%',#{phone},'%')
AND dwd.create_date >= CONCAT(#{startDate},' 00:00:00')
AND dwd.create_date <= CONCAT(#{endDate},' 23:59:59')
ORDER BY dwd.id DESC
LIMIT #{start}, #{rows}

SELECT COUNT(*) FROM d_wallet_detail dwd LEFT JOIN d_wallet dw ON dwd.wallet_id = dw.id LEFT JOIN d_customer_group dcg ON dcg.wallet_id = dw.id LEFT JOIN d_customer dc ON dc.id = dcg.customer_id LEFT JOIN d_group dg ON dg.id = dcg.group_id LEFT JOIN ns_users nu ON nu.id = dwd.creator WHERE 1=1
AND dcg.group_id = #{groupId}
AND dcg.id = #{customerGroupId}
AND dwd.change_type = #{changeType}
AND dc.name LIKE CONCAT('%',#{name},'%')
AND dc.open_id = #{openId}
AND dcg.serial_number LIKE CONCAT('%',#{serialNumber},'%')
AND dc.phone LIKE CONCAT('%',#{phone},'%')
AND dwd.create_date >= CONCAT(#{startDate},' 00:00:00')
AND dwd.create_date <= CONCAT(#{endDate},' 23:59:59')

mapper.update:
UPDATE 
SET
	update_date = NOW()
	,updater = #{updater}
	,update_ip = #{updateIp}

		,sum_money = #{sumMoney}


		,used_money = #{usedMoney}


		,available_money = #{availableMoney}

WHERE id = #{id}
